import { HttpClient } from '@angular/common/http';
import { Component, ViewChild } from '@angular/core';
import { NgForm } from '@angular/forms';
import { Router } from '@angular/router';


@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.css']
})
export class RegisterComponent {
  @ViewChild("myForm")
  form!: NgForm; 
  constructor(private http: HttpClient, private router: Router) {
  }
  onSubmit() {
    this.http.post('http://127.0.0.1:8000/api/register', {
      first_name: this.form.value.first_name,
      last_name: this.form.value.last_name,
      email: this.form.value.email,
      password: this.form.value.password,
      password_confirm: this.form.value.confirm_password,
    }).subscribe({
      next: (response) => {
        console.log(response);
        this.router.navigate(['/login']);
      },
      error: (error) => {
        console.log(error)
        this.router.navigate(['/register']);
        ;
      },
    });
  }
}
<br>
<form (ngSubmit)="onSubmit()" #myForm="ngForm">
  <div class="container">
    <h1 style="text-align: center">Register</h1>
    <p style="text-align: center">Please fill in this form to create an account.</p>
    <hr>

    <label><b>First Name</b></label>
    <input type="text" placeholder="Enter first name" name="first_name" required ngModel>

    <label><b>Last Name</b></label>
    <input type="text" placeholder="Enter last name" name="last_name" required ngModel>

    <label><b>Email Address</b></label>
    <input type="text" placeholder="Enter email" name="email" id="email" required ngModel>

    <label><b>Password</b></label>
    <input type="password" placeholder="Enter password" name="password" required ngModel>

    <label for="psw-repeat"><b>Confirm Password</b></label>
    <input type="password" placeholder="Confirm Password" name="confirm_password" required ngModel>
    <hr>

    <button class="w-100 btn btn-lg btn-primary" type="submit">Register</button>


  </div>
  
  
</form>
